<?php

namespace jobseeker\Bundle\PluginBundle\Library;

use jobseeker\Bundle\PluginBundle\DependencyInjection\UserScope;
use jobseeker\Bundle\PluginBundle\Library\Facebook\Facebook;

class FacebookPlugin extends UserScope
{

    public function __construct()
    {
        $this->config = array(
            "appid" => "",
            "secret" => "",
            "scope" => "",
            "redirect_uri" => "",
            "api_version" => ""
        );
    }

    public function getName()
    {
        return "facebook";
    }

    public function getLogo()
    {
        return "data:image/png;base64,";
    }

    public function getConfigForInstall()
    {
        $config = array();
        $config['appid'] = "***";
        $config['secret'] = "***";
        $config['scope'] = "public_profile,email,user_about_me";
        $config['redirect_uri'] = $this->container->getParameter("host") . "/auth/callback";
        $config['api_version'] = "v2.11";
        return $config;
    }

    public function registerProxy()
    {
        $request = $this->container->get('request_stack')->getCurrentRequest();
        $proxy = new Facebook(
            array(
                "appId" => $this->getConfig("appid"),
                "secret" => $this->getConfig("secret"),
                "sharedSession" => true
            ), $request);
        $this->proxy = $proxy;
    }

    public function getLoginUri()
    {
        return array(
            $this->getName(),
            $this->getProxy()->getLoginUrl(
                array(
                    "scope" => $this->getConfig("scope"),
                    "redirect_uri" => $this->getConfig("redirect_uri"),
                )
            )
        );
    }

    public function authenticate($code = NULL)
    {
        try {
            $token = $this->getProxy()->getAccessToken($code);
            parent::saveToken(json_encode($token));
        } catch (\Exception $e) {
            throw $e;
        }
    }

    public function revokeToken()
    {
        return $this->getProxy()->destroySession();
    }

    public function refreshToken($token)
    {
        return $this->getProxy()->refreshToken($token);
    }

    public function getUser()
    {
        if ($this->getClient() == null) {
            $request = $this->container->get('request_stack')->getCurrentRequest();
            $token = $this->container->get("UserPluginManager")->getToken();
            if ($token['name'] == $this->getName() && $value = $token['value']) {
                $client = new Facebook(array(
                    "appId" => $this->getConfig("appid"),
                    "secret" => $this->getConfig("secret"),
                    "sharedSession" => true
                ), $request);
                $this->setClient($client);
            }
        }

        return $this->getClient()->getUserInfo();
    }

    public function getUserForSso()
    {
        $user = $this->getUser();

        if (false !== $user) {
            return array(
                "source" => $this->getName(),
                "email" => $user['unionid'],
                "password" => $user['unionid'],

            );
        } else {
            return null;
        }
    }

}
